import React, { useState } from "react";
import { axiosWithAuth } from "../../utils/axiosWithAuth";
import { useForm } from "react-hook-form";

const StudentTicketCard = ({ ticket, setTickets, tickets }) => {
  const [edit, toggleEdit] = useState(false);
  const { register, handleSubmit, errors } = useForm();
  const onSubmit = (data) =>
    editTicket(user.id, refreshTicket, data, toggleEdit, edit);
  console.log(errors);

  const editTicket = (id, ticket) => {
    axiosWithAuth()
      .put(`/api/tickets/${id}`, ticket)
      .then((res) => {
        console.log("Ticket Editted");
        toggleEdit(!edit);
      })
      .catch((err) => {
        console.log(err, "Failed to edit Ticket");
      });
  };

  const deleteTicket = (id) => {
    axiosWithAuth()
      .delete(`/api/tickets/${id}`)
      .then((res) => {
        console.log("Ticket Deleted");
        setTickets(tickets.filter((ticket) => ticket.id !== id));
      })
      .catch((err) => {
        console.log(err, "Failed to delete Student");
      });
  };

  return edit === false ? (
    <div>
      <h2>
        {ticket.title} {ticket.description} {ticket.tried} {ticket.category}
      </h2>
      <button onClick={() => deleteTicket(ticket.id)}>Delete</button>
      <button onClick={() => toggleEdit(!edit)}>Edit</button>
    </div>
  ) : (
    <div>
      <form onSubmit={handleSubmit(null)}>
        <input
          type="text"
          placeholder="Title"
          name="Title"
          ref={register({ required: true, min: 2, maxLength: 80 })}
        />
        <textarea
          name="Description"
          placeholder="Description"
          ref={register({ required: true, max: 2, maxLength: 300 })}
        />
        <input
          type="text"
          placeholder="Tried"
          name="Tried"
          ref={register({
            required: true,
            min: 2,
            maxLength: 128,
          })}
        />
        <input
          type="text"
          placeholder="Category"
          name="Category"
          ref={register({ required: true, min: 2, maxLength: 128 })}
        />

        <input type="submit" />
      </form>

      <button onClick={() => toggleEdit(!edit)}>Cancel</button>
    </div>
  );
};

export default StudentTicketCard;
