import React, { useState, useEffect, useContext } from "react";
import { useForm } from "react-hook-form";
import StudentCard from "../student/StudentCard";
import { UserContext } from "../../contexts/AppContext";
import { axiosWithAuth } from "../../utils/axiosWithAuth";

const postNewTicketS = (newTicket, refreshTicket) => {
  axiosWithAuth()
    .post("/api/tickets", newTicket)
    .then((res) => {
      console.log(res.data);
      refreshTicket();
    })
    .catch((err) => {
      console.log(err, "Failed to post new student");
    });
};

const Student = (props) => {
  const [ticket, setTicket] = useState([]);

  const [setUser] = useContext(UserContext);

  const refreshTicket = (props) => {
    axiosWithAuth()
      .get("/api/tickets")
      .then((res) => {
        console.log(res);
      })
      .catch((error) => {
        console.log(error);
      });
  };

  useEffect(() => {
    refreshTicket();
  }, []);

  const submitTicket = (data) => {
    console.log(data);
    postNewTicket(
      {
        title: props.title,
        description: props.description,
        tried: props.tried,
        category: props.category,
      },
      refreshTicket
    );
  };

  const { register, handleSubmit, errors } = useForm();
  console.log(errors);

  return (
    <>
      <form onSubmit={handleSubmit(submitTicket)}>
        <input
          type="text"
          placeholder="Title"
          name="Title"
          ref={register({ required: true, min: 2, maxLength: 80 })}
        />
        <textarea
          name="Description"
          placeholder="Description"
          ref={register({ required: true, max: 2, maxLength: 300 })}
        />
        <input
          type="text"
          placeholder="Tried"
          name="Tried"
          ref={register({
            required: true,
            min: 2,
            maxLength: 128,
          })}
        />
        <input
          type="text"
          placeholder="Category"
          name="Category"
          ref={register({ required: true, min: 2, maxLength: 128 })}
        />

        <input type="submit" />
      </form>

      <div>
        <div className="studentList">
          {/* {ticket.map((student, index) => (
            <StudentCard student={student} refreshStudent={refreshStudent} />
          ))} */}
        </div>
      </div>
    </>
  );
};

export default Student;
