import React, { useState, useEffect, useContext } from "react";
import { useForm } from "react-hook-form";
// import StudentTicketCard from "../student/StudentCard";
import { UserContext } from "../../contexts/AppContext";
import { axiosWithAuth } from "../../utils/axiosWithAuth";

const Student = (props) => {
  const [tickets, setTickets] = useState([]);

  const postNewTicket = (newTicket) => {
    axiosWithAuth()
      .post("/api/tickets", newTicket)
      .then((res) => {
        console.log(res.data);
        setTickets(res.data);
      })
      .catch((err) => {
        console.log(err, "Failed to post new ticket");
      });
  };
  const refreshTicket = (props) => {
    axiosWithAuth()
      .get("/api/tickets")
      .then((res) => {
        console.log(res);
        setTickets(res.data);
      })
      .catch((error) => {
        console.log(error);
      });
  };

  useEffect(() => {
    refreshTicket();
  }, []);

  const submitTicket = (data) => {
    console.log(data);
    postNewTicket(data);
  };

  const { register, handleSubmit, errors } = useForm();
  console.log(errors);

  return (
    <>
      <form onSubmit={handleSubmit(submitTicket)}>
        <input
          type="text"
          placeholder="Title"
          name="title"
          ref={register({ required: true, min: 2, maxLength: 80 })}
        />
        <textarea
          name="description"
          placeholder="Description"
          ref={register({ required: true, max: 2, maxLength: 300 })}
        />
        <input
          type="text"
          placeholder="Tried"
          name="tried"
          ref={register({
            required: true,
            min: 2,
            maxLength: 128,
          })}
        />
        <input
          type="text"
          placeholder="Category"
          name="category"
          ref={register({ required: true, min: 2, maxLength: 128 })}
        />

        <input type="submit" />
      </form>

      <div>
        <div className="studentList">
          {tickets.map((ticket, index) => (
            <StudentTicketCard tickets={tickets} />
          ))}
        </div>
      </div>
    </>
  );
};

export default Student;
